import jwt_decode from 'jwt-decode';

const token = 'eyJhbGciOiJodHRwOi8vd3d3LnczLm9yZy8yMDAxLzA0L3htbGRzaWctbW9yZSNobWFjLXNoYTI1NiIsInR5cCI6IkpXVCJ9..prA4NzlCC3b302qoJRN_KxG3rjzBEqAKGxQMRekdJfc';

class AuthService {
    getToken() {
        // return localStorage.getItem('token');

        return token;
    }

    checkToken() {
        if (!this.getToken()) {
            this.logout();
        }
    }

    getUserId() {
        const tokenData: any = jwt_decode(this.getToken()); 
        const userId = tokenData['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/nameidentifier'];
        return userId;
    }

    getUserName() {
        const tokenData: any = jwt_decode(this.getToken());
        const userId = tokenData['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name'];
        return userId;
    }

    logout() {
        localStorage.removeItem('token');
        window.location.pathname = '/Login';
    }
}

const authService = new AuthService();
export default authService;