import { Injectable } from '@angular/core';
import jwt_decode from 'jwt-decode';

const token = 'eyJhbGciOiJodHRwOi8vd3d3LnczLm9yZy8yMDAxLzA0L3htbGRzaWctbW9yZSNobWFjLXNoYTI1NiIsInR5cCI6IkpXVCJ9..prA4NzlCC3b302qoJRN_KxG3rjzBEqAKGxQMRekdJfc';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  getToken() {
    return localStorage.getItem('token');

    // return token;
  }

  checkToken() {
    if (!this.getToken()) {
      this.logout();
    }
  }

  getUserName() {
    const tokenData: any = jwt_decode(this.getToken()); 
    const userName = tokenData['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name'];
    return userName;
  }

  logout() {
    localStorage.removeItem('token');
    window.location.pathname = '/Login';
  }
}
